// Security Event Handler
const event = $input.item.json;
const severity = event.severity || 'medium';
const location = event.location || 'Unknown';

// Determine actions based on security event
let actions = [];
let notifications = [];

switch(event.subType) {
  case 'motion_detected':
    if (event.armed) {
      actions.push({
        type: 'alarm',
        action: 'trigger',
        zone: location
      });
      notifications.push({
        priority: 'high',
        title: 'ðŸš¨ INTRUSION DETECTED',
        message: `Motion detected in ${location} while system armed`,
        channels: ['slack', 'sms', 'email']
      });
    } else {
      // Just log for monitoring
      actions.push({
        type: 'log',
        message: `Motion in ${location}`
      });
    }
    break;

  case 'door_opened':
    if (event.armed && !event.authorized) {
      actions.push({
        type: 'alarm',
        action: 'trigger',
        zone: location
      });
      actions.push({
        type: 'lights',
        action: 'flash',
        rooms: ['all']
      });
      notifications.push({
        priority: 'critical',
        title: 'ðŸšª UNAUTHORIZED ACCESS',
        message: `${location} opened without authorization`,
        channels: ['slack', 'sms', 'phone']
      });
    }
    break;

  case 'window_broken':
    actions.push({
      type: 'alarm',
      action: 'trigger',
      zone: location
    });
    actions.push({
      type: 'lights',
      action: 'on',
      rooms: ['all']
    });
    actions.push({
      type: 'camera',
      action: 'record',
      location: location
    });
    notifications.push({
      priority: 'critical',
      title: 'ðŸ”´ GLASS BREAK DETECTED',
      message: `Window broken in ${location}`,
      channels: ['slack', 'sms', 'phone', 'police']
    });
    break;
}

return {
  json: {
    originalEvent: event,
    actions: actions,
    notifications: notifications,
    timestamp: new Date().toISOString()
  }
};

// Energy Event Handler
const event = $input.item.json;
const usage = event.currentUsage || 0;
const threshold = event.threshold || 5000;
const cost = event.estimatedCost || 0;

let actions = [];
let notifications = [];

// Calculate usage percentage
const usagePercent = Math.round((usage / threshold) * 100);

if (usagePercent > 150) {
  // Critical usage
  actions.push({
    type: 'reduce_load',
    level: 'aggressive',
    targets: ['hvac', 'water_heater', 'non_essential']
  });
  notifications.push({
    priority: 'high',
    title: 'âš¡ CRITICAL ENERGY USAGE',
    message: `Usage at ${usagePercent}% of threshold (${usage}W)`,
    details: `Estimated cost: $${cost}/hour`,
    channels: ['slack', 'email']
  });
} else if (usagePercent > 100) {
  // Over threshold
  actions.push({
    type: 'reduce_load',
    level: 'moderate',
    targets: ['non_essential']
  });
  notifications.push({
    priority: 'medium',
    title: 'âš ï¸ High Energy Usage',
    message: `Usage at ${usagePercent}% of threshold`,
    channels: ['slack']
  });
}

// Peak hours management
const hour = new Date().getHours();
if ((hour >= 16 && hour <= 20) && usagePercent > 80) {
  actions.push({
    type: 'shift_load',
    message: 'Peak hours - shifting non-critical loads'
  });
}

return {
  json: {
    originalEvent: event,
    actions: actions,
    notifications: notifications,
    metrics: {
      usage: usage,
      threshold: threshold,
      percentage: usagePercent,
      cost: cost
    },
    timestamp: new Date().toISOString()
  }
};

// Transform actions into MCP calls
const actions = $input.item.json.actions || [];

return actions.map(action => {
  let mcpTool = '';
  let mcpParams = {};

  switch(action.type) {
    case 'alarm':
      mcpTool = action.action === 'trigger' ? 'trigger_alarm' : 'silence_alarm';
      mcpParams = { zone: action.zone };
      break;

    case 'lights':
      if (action.action === 'flash') {
        mcpTool = 'flash_lights';
      } else {
        mcpTool = 'control_room_lights';
      }
      mcpParams = { 
        rooms: action.rooms,
        action: action.action 
      };
      break;

    case 'reduce_load':
      mcpTool = 'manage_energy_loads';
      mcpParams = {
        level: action.level,
        targets: action.targets
      };
      break;

    case 'camera':
      mcpTool = 'control_camera';
      mcpParams = {
        location: action.location,
        action: action.action
      };
      break;
  }

  return {
    json: {
      action: {
        name: mcpTool,
        arguments: mcpParams
      },
      actionId: `action-${Date.now()}-${Math.random()}`
    }
  };
});

// Route notifications to appropriate channels
const notifications = $input.item.json.notifications || [];

return notifications.map(notification => {
  const outputs = [];

  notification.channels.forEach(channel => {
    outputs.push({
      json: {
        channel: channel,
        priority: notification.priority,
        title: notification.title,
        message: notification.message,
        details: notification.details || '',
        timestamp: new Date().toISOString()
      }
    });
  });

  return outputs;
}).flat();

// Generate analytics report
const stats = $input.all();

let report = {
  title: 'ðŸ“Š Hourly Home Analytics Report',
  timestamp: new Date().toISOString(),
  summary: [],
  recommendations: []
};

stats.forEach(stat => {
  const data = stat.json;

  if (data.event_type === 'energy') {
    report.summary.push(`âš¡ Energy: ${data.event_count} events, Avg: ${Math.round(data.avg_usage)}W, Max: ${Math.round(data.max_usage)}W`);

    if (data.avg_usage > 4000) {
      report.recommendations.push('Consider load shifting during peak hours');
    }
  } else if (data.event_type === 'security') {
    report.summary.push(`ðŸ”’ Security: ${data.event_count} events`);

    if (data.event_count > 10) {
      report.recommendations.push('High security activity detected - review logs');
    }
  }
});

return {
  json: report
};
